package pattern_programs;

public class B {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=5;i++) //for rows
{
	for(int j=1;j<=i;j++)  //for star
	{
		System.out.print("* ");
	}
	System.out.println();
}
	}

}
